import React from 'react'
import { 
    Switch,
    useColorMode,
    Box,
    Stack,
    HStack,
    Text
 } from '@chakra-ui/react'


function Switcher(){
    const {colorMode, toggleColorMode} = useColorMode()
    return(
        <HStack>
            <Text
            fontFamily="Inter">{colorMode == 'light' ? 'Dark Mode' : 'Light Mode'}</Text>
            <Switch onChange={toggleColorMode} /> 
        </HStack>
    )
}

export default Switcher
