import React from 'react'
import {
    SimpleGrid,
    Box,
    Flex,
    Image,
    Text
} from '@chakra-ui/react'

function OverviewMain(props){
    const dataItems = props.dataItems;
    const dataItem = dataItems.map((sm, index)=>{
        return(
            <Box
            h="100px"
            key={index}
            bgColor="hsl(227, 47%, 96%)"
            p={5}
            >
                <Flex
                direction="row"
                justify="space-between"
                mb={4}
                >
                    <Text>{sm.action_sm}</Text>
                    <Image src={sm.logo_sm} alt="social media"/>
                </Flex>
                <Flex
                direction="row"
                justify="space-between">
                    <Text>{sm.num_action}</Text>
                    <Flex
                    direction="row">
                        <Image src={sm.icon} alt="social media"/>
                        <Text>Texto</Text>
                    </Flex>
                </Flex>
            </Box>
        )
    })
    return(
        <SimpleGrid columns={4} spacing={10}>
            {dataItem}
        </SimpleGrid>
    )
}

export default OverviewMain;
