import {
    extendTheme,
    useStyleConfig
} from '@chakra-ui/react'
import colors from './custom/colors'
import config from './custom/config'
import DashboardMain from '../components/main_dashboard'

const theme = extendTheme({
    colors,
    config,
    fonts: {
        body: 'Inter',
    },
    components: {
        DashboardMain
    }
})



export default theme
