import React from 'react'
import { 
    Switch,
    useColorMode,
    HStack,
    Text
 } from '@chakra-ui/react'


function Switcher(){
    const {colorMode, toggleColorMode} = useColorMode()
    return(
        <HStack>
            <Text
            color="gray.500"
            fontSize="sm"
            fontWeight="bold"
            >{colorMode == 'light' ? 'Dark Mode' : 'Light Mode'}</Text>
            <Switch onChange={toggleColorMode} colorScheme="linear-gradient(hsl(210, 78%, 56%),hsl(146, 68%, 55%))"/> 
        </HStack>
    )
}

export default Switcher
